     public static int maxLength(String str){
        // code here
        if(str.length()==1){
            return 0;
        }
        
        Stack<Character> stack= new Stack<>();
        
        char closing=')';
        
        String ans ="";
        
        for(int i =0;i<str.length();i++){
            
            if(str.charAt(i)==closing){
                
                if(stack.empty()!=true && stack.peek()=='('){
                    stack.pop();
                    ans=ans+"()";
                    continue;
                }   
                
            }
            
            stack.push(str.charAt(i));
            
            
        }
        
        return ans.length();
        
        
    }
